/*
	Class defines map zones. Returns true|false by zone ID and coordinates,
	ex.: isSpecialArea(AREA_ID_TARGETING_FORBIDDEN, aWorldX_num, aWorldY_num).
	
	Custom zones can be drawn with SpecialAreasMapEditor, then can be added here like AREA_ID_TARGETING_FORBIDDEN
	Others from other implementation types:
	1. Method does not compare coordinates (even if zone is very difficult). Point coordinate simply checks with 2 divisions (because here there is a table of value instead of rects/poligons)
	2. Easier to draw zone via editor
*/

const CELL_SIZE = 20;
const WIDTH = Math.trunc(960 / CELL_SIZE);
const HEIGHT = Math.trunc(540 / CELL_SIZE);

const AREA_ID_TARGETING_FORBIDDEN = "AREA_ID_TARGETING_FORBIDDEN";
const AREA_ID_BEHIND_WALLS = "AREA_ID_BEHIND_WALLS";

const AREAS_IDS =
	[
		AREA_ID_TARGETING_FORBIDDEN,
		AREA_ID_BEHIND_WALLS
	]

const AREAS_IDS_COUNT = AREAS_IDS.length;

const BIT_MAPS =
{
	AREA_ID_TARGETING_FORBIDDEN:
		[
			//map 0
			"",
			//map 1
			"",
			//map 2
			"",
			//map 3
			"",
		],

	AREA_ID_BEHIND_WALLS:
		[
			//map 0
			"",
			//map 1
			"",
			//map 2
			"",
			//map 3
			"",
		],
}

import { APP } from '../../../../../common/PIXI/src/dgphoenix/unified/controller/main/globals';
import MapsController from '../../controller/uis/maps/MapsController';

class SpecialAreasMap
{
	static get CELL_SIZE()							{ return CELL_SIZE }
	static get WIDTH()								{ return WIDTH }
	static get HEIGHT()								{ return HEIGHT }

	static get AREA_ID_TARGETING_FORBIDDEN()		{ return AREA_ID_TARGETING_FORBIDDEN }
	static get AREA_ID_BEHIND_WALLS()				{ return AREA_ID_BEHIND_WALLS }

	constructor()
	{
		this._fCells_obj_arr_arr = [];

		for (let y = 0; y < HEIGHT; y++)
		{
			this._fCells_obj_arr_arr[y] = [];

			for (let x = 0; x < WIDTH; x++)
			{
				this._fCells_obj_arr_arr[y][x] = {};
			}
		}

		APP.gameScreen.mapsController.on(MapsController.EVENT_CURRENT_MAP_ID_UPDATED, this._onMapChange, this);
	}

	_onMapChange(aEvent_obj)
	{
		let lLevelIndex_int = APP.gameScreen.mapsController.info.mapId - 1;

		for (let i = 0; i < AREAS_IDS_COUNT; i++)
		{
			this._applyAreaBitMap(AREAS_IDS[i], BIT_MAPS[AREAS_IDS[i]][lLevelIndex_int]);
		}
	}

	_applyAreaBitMap(aAreaId_str, aBitMap_str)
	{
		let i = 0;

		for (let y = 0; y < HEIGHT; y++)
		{
			for (let x = 0; x < WIDTH; x++)
			{
				let lValue_int = Number.parseInt(aBitMap_str[i]);

				this._fCells_obj_arr_arr[y][x][aAreaId_str] = (lValue_int === 1);

				i++;
			}
		}
	}

	getCell(aWorldX_num, aWorldY_num)
	{
		let lX_int = Math.trunc(aWorldX_num / CELL_SIZE);
		let lY_int = Math.trunc(aWorldY_num / CELL_SIZE);

		if (
			lX_int < 0 ||
			lY_int < 0 ||
			lX_int >= WIDTH ||
			lY_int >= HEIGHT
		)
		{
			return null;
		}

		return this._fCells_obj_arr_arr[lY_int][lX_int];
	}

	isSpecialArea(aAreaId_str, aWorldX_num, aWorldY_num)
	{
		return false; // Have not SA in MSX maps

		let lCell_obj = this.getCell(aWorldX_num, aWorldY_num);

		if (!lCell_obj)
		{
			return false;
		}

		return lCell_obj[aAreaId_str];
	}
}

export default SpecialAreasMap